import DatePicker from "react-date-picker";
import { useState } from "react";
import { PictureAsPdf } from "@material-ui/icons";
import { useForm } from "react-hook-form";
import axios from 'axios'
import "./Add.scss";

const Add = () => {
  const [dateValue, setDateValue] = useState(new Date());
  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    // let fileImages = document.querySelector('#files')
    console.log(files.file)
    let config = {
      url: "http://localhost:3031/createnews", // TODO: update with correct API
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      data: { ...data, date: dateValue },
    };
    console.log(data)

    axios(config)
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div className="form-wrapper">
      <div className="form-search">
        <span className="title"></span>

        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="mb-3 row">
            <label className="col-sm-2 col-form-label">Category</label>
            <div className="col-sm-6">
              <select className="form-select input-custom" {...register("category")} name="category">
                <option disabled>Select Category</option>
                <option value="1">News</option>
                <option value="2">IT Group</option>
                <option value="3">Infographic</option>
              </select>
            </div>
          </div>

          <div className="mb-3 row">
            <label className="col-sm-2 col-form-label">Title</label>
            <div className="col-sm-6">
              <input type="text" className="form-control input-custom" placeholder="Title" {...register("title")} name="title" />
            </div>
          </div>

          <div className="mb-3 row">
            <label className="col-sm-2 col-form-label">Excerpt</label>
            <div className="col-sm-6">
              <input type="text" className="form-control input-custom" placeholder="Excerpt" {...register("excerpt")} name="excerpt" />
            </div>
          </div>

          <div className="mb-3 row">
            <label className="col-sm-2 col-form-label">Description</label>
            <div className="col-sm-6">
              <textarea className="message" placeholder="type your message" rows={4} {...register("description")} name="description"></textarea>
            </div>
          </div>

          <div className="mb-3 row">
            <label className="col-sm-2 col-form-label">Images</label>
            <div className="col-sm-6">
              <input id="fileImages" type="file" accept="images/*" className="form-control input-custom" placeholder="Images" {...register("images")} name="images" />
            </div>
          </div>

          <div className="mb-3 row">
            <label className="col-sm-2 col-form-label">Date</label>
            <div className="col-sm-6">
              <DatePicker className="custom-input-date" onChange={setDateValue} value={dateValue} maxDetail="month" />
            </div>
          </div>
          <div className="row">
            <div className="col-sm-6 offset-sm-2">
              <button type="submit" className="btn btn-primary px-4">
                Submit
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Add;
